----------------------------------------------------------------------------------------- 

-- Creación Tabla Cursos Y PK 

CREATE TABLE cursos (
    cursoid            INTEGER NOT NULL,
    nombrecurso        VARCHAR2(100 CHAR)
);

ALTER TABLE cursos ADD CONSTRAINT cursos_pk PRIMARY KEY ( cursoid );


-- Creación Tabla Alumnos

CREATE TABLE alumnos (
    rutalumno                  INTEGER NOT NULL,
    nombrealumno               VARCHAR2(50 CHAR),
    primerapellidoalumno       VARCHAR2(50 CHAR),
    segundoapellidoalumno      VARCHAR2(50 CHAR),
    cursos_cursoid             INTEGER NOT NULL
);

ALTER TABLE alumnos ADD CONSTRAINT alumnos_pk PRIMARY KEY ( rutalumno );

-- Creación Tabla Test

CREATE TABLE test (
    testid                INTEGER NOT NULL,
    nombretest            VARCHAR2(150 CHAR),
    descripcion           VARCHAR2(255 CHAR),
    programa              VARCHAR2(150 CHAR),
    unidad                VARCHAR2(50 CHAR),
    autor                 VARCHAR2(50 CHAR),
    fechacreacion         DATE
);

ALTER TABLE test ADD CONSTRAINT test_pk PRIMARY KEY ( testid );

-- Creación Tabla Preguntas

CREATE TABLE preguntas (
    preguntaid  INTEGER NOT NULL,
    test_testid INTEGER NOT NULL,
    enunciado   VARCHAR2(255 CHAR) NOT NULL,
    opcion1     VARCHAR2(255 CHAR),
    opcion2     VARCHAR2(255 CHAR),
    opcion3     VARCHAR2(255 CHAR),
    opcion4     VARCHAR2(255 CHAR),
    puntaje100  NUMBER(2,2),
    puntaje50   NUMBER(2,2),
    respuesta1  CHAR(1),
    respuesta2  CHAR(1)
);

ALTER TABLE preguntas ADD CONSTRAINT preguntas_pk PRIMARY KEY ( preguntaid );

-- Creación Tabla Evaluaciones

CREATE TABLE evaluaciones (
    evaluacionid          INTEGER NOT NULL,
    rutalumno             INTEGER NOT NULL,
    testalumno            INTEGER NOT NULL,
    respuesta1a           NUMBER(1,0),
    respuesta1b           NUMBER(1,0),
    respuesta1c           NUMBER(1,0),
    respuesta1d           NUMBER(1,0),
    respuesta2a           NUMBER(1,0),
    respuesta2b           NUMBER(1,0),
    respuesta2c           NUMBER(1,0),
    respuesta2d           NUMBER(1,0),
    respuesta3a           NUMBER(1,0),
    respuesta3b           NUMBER(1,0),
    respuesta3c           NUMBER(1,0),
    respuesta3d           NUMBER(1,0),
    respuesta4a           NUMBER(1,0),
    respuesta4b           NUMBER(1,0),
    respuesta4c           NUMBER(1,0),
    respuesta4d           NUMBER(1,0),
    respuesta5a           NUMBER(1,0),
    respuesta5b           NUMBER(1,0),
    respuesta5c           NUMBER(1,0),
    respuesta5d           NUMBER(1,0),
    respuesta6a           NUMBER(1,0),
    respuesta6b           NUMBER(1,0),
    respuesta6c           NUMBER(1,0),
    respuesta6d           NUMBER(1,0),
    respuesta7a           NUMBER(1,0),
    respuesta7b           NUMBER(1,0),
    respuesta7c           NUMBER(1,0),
    respuesta7d           NUMBER(1,0)
);

ALTER TABLE evaluaciones ADD CONSTRAINT evaluaciones_pk PRIMARY KEY ( evaluacionid );

--------------------------------------------------------------------------------------------------
-- Creación de FK

ALTER TABLE alumnos
    ADD CONSTRAINT alumnos_cursoid_fk FOREIGN KEY ( cursos_cursoid )
        REFERENCES cursos ( cursoid );
       
ALTER TABLE preguntas
    ADD CONSTRAINT preguntas_testid_fk FOREIGN KEY ( test_testid )
        REFERENCES test ( testid );
        
ALTER TABLE evaluaciones
    ADD CONSTRAINT evaluaciones_rutalumno_fk FOREIGN KEY ( rutalumno )
        REFERENCES alumnos ( rutalumno );
        
ALTER TABLE evaluaciones
    ADD CONSTRAINT evaluaciones_testalumno_fk FOREIGN KEY ( testalumno )
        REFERENCES test ( testid );
