-- CONSULTAS

-- Pregunta 1: Conocer el número de evaluaciones por curso.

SELECT NOMBRECURSO, COUNT(EVALUACIONES.TESTALUMNO) EVALUACIONES

FROM ALUMNOS
JOIN CURSOS ON alumnos.cursos_cursoid = cursos.cursoid
JOIN EVALUACIONES ON EVALUACIONES.RUTALUMNO = ALUMNOS.RUTALUMNO

GROUP BY nombrecurso
ORDER BY nombrecurso
;

-- Pregunta 2: Conocer los cursos sin evaluaciones.

SELECT NOMBRECURSO, TESTALUMNO SIN_EVALUACIONES

FROM ALUMNOS
JOIN CURSOS ON alumnos.cursos_cursoid = cursos.cursoid
JOIN EVALUACIONES ON EVALUACIONES.RUTALUMNO = ALUMNOS.RUTALUMNO

WHERE EVALUACIONES.TESTALUMNO = NULL
;


--Pregunta 3: Determinar las evaluaciones con deficiencia. Una evaluación es deficiente:
--a. Si no tiene preguntas.
--b. Si hay preguntas con 2 ó menos alternativas
--c. Si todas las alternativas son correctas o si todas las alternativas son incorrectas.


--Pregunta 4: Determinar cuántos alumnos hay en cada curso.

SELECT NOMBRECURSO, COUNT(CURSOS.CURSOID) AS ALUMNOS

FROM CURSOS 
JOIN ALUMNOS ON ALUMNOS.CURSOS_CURSOID = CURSOS.CURSOID 

GROUP BY NOMBRECURSO
ORDER BY NOMBRECURSO
;


--Pregunta 5: Obtener el puntaje no normalizado de cada evaluación. El puntaje no
--normalizado ha sido definido (requerimiento) como: P = buenas – malas/4. Si un alumno
--no contesta en una pregunta exactamente lo mismo que se ha definido como correcto,
--la pregunta cuenta como mala a menos que el alumno haya omitido.


--Pregunta 6: Obtener el puntaje normalizado, o sea, de 1,0 a 7,0.

SELECT RUTALUMNO,TESTALUMNO, PROGRAMA, 
(((RESPUESTA1A * PUNTAJE100)+(RESPUESTA1B * PUNTAJE50)) +
((RESPUESTA2B * PUNTAJE100)+(RESPUESTA2C * PUNTAJE50)) +
((RESPUESTA3C * PUNTAJE100)+(RESPUESTA3D * PUNTAJE50)) +
((RESPUESTA4D * PUNTAJE100)+(RESPUESTA4A * PUNTAJE50)) +
((RESPUESTA5A * PUNTAJE100)+(RESPUESTA5C * PUNTAJE50)) +
((RESPUESTA6B * PUNTAJE100)+(RESPUESTA6A * PUNTAJE50)) +
((RESPUESTA7C * PUNTAJE100)+(RESPUESTA7D * PUNTAJE50))) NOTA

FROM EVALUACIONES

JOIN TEST ON TESTID = TESTALUMNO
JOIN PREGUNTAS ON TEST_TESTID = TESTID


GROUP BY RUTALUMNO, TESTALUMNO, PROGRAMA, 
(((RESPUESTA1A * PUNTAJE100)+(RESPUESTA1B * PUNTAJE50)) +
((RESPUESTA2B * PUNTAJE100)+(RESPUESTA2C * PUNTAJE50)) +
((RESPUESTA3C * PUNTAJE100)+(RESPUESTA3D * PUNTAJE50)) +
((RESPUESTA4D * PUNTAJE100)+(RESPUESTA4A * PUNTAJE50)) +
((RESPUESTA5A * PUNTAJE100)+(RESPUESTA5C * PUNTAJE50)) +
((RESPUESTA6B * PUNTAJE100)+(RESPUESTA6A * PUNTAJE50)) +
((RESPUESTA7C * PUNTAJE100)+(RESPUESTA7D * PUNTAJE50)))

ORDER BY TESTALUMNO
;

--Pregunta 7: Nombre de estudiantes de un curso determinado que aprueban una
--evaluación determinada (donde la nota de aprobación mínima es un 4,0).

SELECT RUTALUMNO,TESTALUMNO, PROGRAMA, 
(((RESPUESTA1A * PUNTAJE100)+(RESPUESTA1B * PUNTAJE50)) +
((RESPUESTA2B * PUNTAJE100)+(RESPUESTA2C * PUNTAJE50)) +
((RESPUESTA3C * PUNTAJE100)+(RESPUESTA3D * PUNTAJE50)) +
((RESPUESTA4D * PUNTAJE100)+(RESPUESTA4A * PUNTAJE50)) +
((RESPUESTA5A * PUNTAJE100)+(RESPUESTA5C * PUNTAJE50)) +
((RESPUESTA6B * PUNTAJE100)+(RESPUESTA6A * PUNTAJE50)) +
((RESPUESTA7C * PUNTAJE100)+(RESPUESTA7D * PUNTAJE50)))  NOTA

FROM EVALUACIONES

JOIN TEST ON TESTID = TESTALUMNO
JOIN PREGUNTAS ON TEST_TESTID = TESTID

GROUP BY RUTALUMNO, TESTALUMNO, PROGRAMA, 
(((RESPUESTA1A * PUNTAJE100)+(RESPUESTA1B * PUNTAJE50)) +
((RESPUESTA2B * PUNTAJE100)+(RESPUESTA2C * PUNTAJE50)) +
((RESPUESTA3C * PUNTAJE100)+(RESPUESTA3D * PUNTAJE50)) +
((RESPUESTA4D * PUNTAJE100)+(RESPUESTA4A * PUNTAJE50)) +
((RESPUESTA5A * PUNTAJE100)+(RESPUESTA5C * PUNTAJE50)) +
((RESPUESTA6B * PUNTAJE100)+(RESPUESTA6A * PUNTAJE50)) +
((RESPUESTA7C * PUNTAJE100)+(RESPUESTA7D * PUNTAJE50)))

HAVING (((RESPUESTA1A * PUNTAJE100)+(RESPUESTA1B * PUNTAJE50)) +
((RESPUESTA2B * PUNTAJE100)+(RESPUESTA2C * PUNTAJE50)) +
((RESPUESTA3C * PUNTAJE100)+(RESPUESTA3D * PUNTAJE50)) +
((RESPUESTA4D * PUNTAJE100)+(RESPUESTA4A * PUNTAJE50)) +
((RESPUESTA5A * PUNTAJE100)+(RESPUESTA5C * PUNTAJE50)) +
((RESPUESTA6B * PUNTAJE100)+(RESPUESTA6A * PUNTAJE50)) +
((RESPUESTA7C * PUNTAJE100)+(RESPUESTA7D * PUNTAJE50))) > 4


ORDER BY TESTALUMNO
;

--Pregunta 8: Nota promedio de los estudiantes de un curso determinado, para una
--evaluación de terminada.

SELECT PROGRAMA, TESTALUMNO, AVG
((((RESPUESTA1A * PUNTAJE100)+(RESPUESTA1B * PUNTAJE50)) +
((RESPUESTA2B * PUNTAJE100)+(RESPUESTA2C * PUNTAJE50)) +
((RESPUESTA3C * PUNTAJE100)+(RESPUESTA3D * PUNTAJE50)) +
((RESPUESTA4D * PUNTAJE100)+(RESPUESTA4A * PUNTAJE50)) +
((RESPUESTA5A * PUNTAJE100)+(RESPUESTA5C * PUNTAJE50)) +
((RESPUESTA6B * PUNTAJE100)+(RESPUESTA6A * PUNTAJE50)) +
((RESPUESTA7C * PUNTAJE100)+(RESPUESTA7D * PUNTAJE50))))  NOTA

FROM EVALUACIONES

JOIN TEST ON TESTID = TESTALUMNO
JOIN PREGUNTAS ON TEST_TESTID = TESTID

GROUP BY PROGRAMA, TESTALUMNO,
(((RESPUESTA1A * PUNTAJE100)+(RESPUESTA1B * PUNTAJE50)) +
((RESPUESTA2B * PUNTAJE100)+(RESPUESTA2C * PUNTAJE50)) +
((RESPUESTA3C * PUNTAJE100)+(RESPUESTA3D * PUNTAJE50)) +
((RESPUESTA4D * PUNTAJE100)+(RESPUESTA4A * PUNTAJE50)) +
((RESPUESTA5A * PUNTAJE100)+(RESPUESTA5C * PUNTAJE50)) +
((RESPUESTA6B * PUNTAJE100)+(RESPUESTA6A * PUNTAJE50)) +
((RESPUESTA7C * PUNTAJE100)+(RESPUESTA7D * PUNTAJE50)))

HAVING  PROGRAMA = 'JAVA FULL STACK TRAINEE' AND TESTALUMNO = 2 

;

